import React from 'react'
import './Header.css';
import { Link } from 'react-router-dom';


const Header = () => {
  return (
    < >
    <div className='wrap'>
    <nav className="navbar">
      <div className='Logo'>
        <Link to='/' className='Logo-img'> LOGO<img /> </Link>
      </div>
      <div className='navbar'>
        <ul>
          <CustomLink to="/About">關於赤弦</CustomLink>
          <CustomLink to="/Information">比賽資訊</CustomLink>
          <CustomLink to="/Sponsor">贊助廠商</CustomLink>
        </ul>
      </div>
</nav>
</div>
    </ >
  )
}

function CustomLink({href,children,...props}){
  const path = window.location.pathname
  console.log(window.location)
  return(
    <li className={path === href ? "active":""}>
      <Link to={href} {...props}>
        {children}
      </Link>
    </li>
  )

}

export default Header
