import React from 'react'

import { useEffect } from 'react'
import {withRouter} from 'react-router-dom'
// 首先，程式碼匯入了兩個模組：
// useEffect：這是 React 提供的一個鉤子函式，用於處理副作用操作，
// 例如在元件掛載、更新、解除掛載時執行一些操作。
// withRouter：這是來自於 'react-router-dom' 庫的一個高階元件，
// 它可以將路由相關的資訊（例如 history）注入到元件的 props 中。


function ScrollToTop({history}){
    useEffect(() => {
        const unlisten = history.listen(() => {
            window.scrollTo(0,0);
        });
        return() =>{
            unlisten();
        }
    },[])
    return(null);
}

export default withRouter(ScrollToTop);
